pre_install:
try {
  New-Item $dir\laravel.bat -type file -force -value "`@ECHO OFF
  php %~dp0laravel %*"
  }
  catch {
    abort 'PHP is not installed | run scoop install php'
    }

post_install:
try {
      composer install -d $dir
  }
  catch {
    abort 'Composer is not installed | run scoop install php'
    }
